/* eslint-disable jsx-a11y/anchor-is-valid */
import { useState } from "react";
import {
  MEDIUM_SCREEN,
  SML_SCREEN,
  STANDART_SCREEN,
} from "../../utils/screen-size";
import Popup from "../Popup/Popup";
import "./Header.css";

export default function Header() {
  const [width, setWidth] = useState(
    window.innerWidth
  );
  const [modalActive, setModalActive] =
    useState(false);

  window.addEventListener(
    "resize",
    function (event) {
      setWidth(window.innerWidth);
    }
  );

  if (width <= SML_SCREEN || MEDIUM_SCREEN) {
    return (
      <header
        className="header__burger"
        id="Header"
        onClick={() => setModalActive(true)}
      ></header>
    );
  }
  if (width <= STANDART_SCREEN) {
    return (
      <header
        className={
          width < SML_SCREEN || MEDIUM_SCREEN
            ? "header__burger"
            : "header"
        }
        id="Header"
        onClick={() => setModalActive(true)}
      >
        <Popup
          active={modalActive}
          setActive={setModalActive}
        />
        <ul className="header__ul">
          <li className="header__li">
            <a
              href="#Team"
              className="header__li-link"
            >
              Наша команда
            </a>
          </li>
          <li className="header__li">
            <a
              href="#Works"
              className="header__li-link"
            >
              Наши проекты
            </a>
          </li>
          <li className="header__li">
            <a
              href="#"
              className="header__li-link"
            >
              Услуги
            </a>
          </li>
          <li className="header__li">
            <a
              href="#Footer"
              className="header__li-link"
            >
              Контакты
            </a>
          </li>
        </ul>
      </header>
    );
  }
}
