/* eslint-disable jsx-a11y/anchor-is-valid */
import { useState, useEffect } from "react";
import { SML_SCREEN } from "../../utils/screen-size";
import "./Header.css";

export default function Header() {
  const [isOpen, setIsOpen] = useState(false);

  useEffect(() => {
    function closeMenuResize() {
      if (
        document.documentElement.clientWidth >
        SML_SCREEN
      ) {
        setIsOpen(false);
        window.removeEventListener(
          "resize",
          closeMenuResize
        );
      }
    }
    if (isOpen) {
      window.addEventListener(
        "resize",
        closeMenuResize
      );
      return () =>
        window.removeEventListener(
          "resize",
          closeMenuResize
        );
    }
  }, [isOpen]);

  return (
    <header
      className={`${
        isOpen ? "header__burger" : ""
      }`}
      id="Header"
    >
      <ul className="header__ul">
        <li className="header__li">
          <a
            href="#Team"
            className="header__li-link"
          >
            Наша команда
          </a>
        </li>
        <li className="header__li">
          <a
            href="#Works"
            className="header__li-link"
          >
            Наши проекты
          </a>
        </li>
        <li className="header__li">
          <a
            href="#"
            className="header__li-link"
          >
            Услуги
          </a>
        </li>
        <li className="header__li">
          <a
            href="#Footer"
            className="header__li-link"
          >
            Контакты
          </a>
        </li>
      </ul>
    </header>
  );
}
